import React from "react";
import "./detail.css";
import Icon from "../../assets/ForkIcon";
import { connect } from "react-redux";
import { setSelectedItem } from "../../redux/store";

function Detail({ selectedItem, setSelectedItem }) {
  return (
    <div id="detail-container">
      {!selectedItem ? (
        <div id="no-data-cont">
          <Icon />
          <span id="icon-detail">Select a restaurant to display it here!</span>
        </div>
      ) : (
        <div id="data-cont">
            <span className="details-data-row big-size-row">{selectedItem.name}</span>
            <span className="details-data-row">Category: {selectedItem.categories[0].alias}</span>
            <span className="details-data-row">Phone: {selectedItem.display_phone}</span>
            <span className="details-data-row">{selectedItem.is_closed ? "Is closed now" : "Is open now"}</span>
            <span className="details-data-row">Address{selectedItem.location.address1}, {selectedItem.location.city}</span>
            <span className="details-data-row">Price range:{selectedItem.price}</span>
            <span className="details-data-row">Rating({selectedItem.reviews}): {selectedItem.rating}</span>
        </div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => ({
  selectedItem: state.selectedItem,
});

const mapDispatchToProps = (dispatch) => ({
  setSelectedItem: (item) => dispatch(setSelectedItem(item)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Detail);
