import React, { useState, useEffect } from 'react';
import './main_page.css'
import SideBar from './side-Bar/SideBar';
import List from './list/List';
import Detail from './detail/Detail';
import { getRestaurants } from '../../redux/store';
import { connect } from 'react-redux';


function MainPage(props) {
    const [page, setPage] = useState("restaurant")

    useEffect( () => {
      }, [])
    return (
        <div id="main-page-container">
            <SideBar setPage={setPage} page={page} />
            <List page={page} data={props.data}/>
            <Detail />
        </div>
    )
}


const mapStateToProps = state => ({
    active: state.active,
    address: state.address,
    unreadNotifications: state.unreadNotifications
})

//Map your action creators to your props.
const mapDispatchToProps = dispatch => ({
    getActiveMessages: () => dispatch(getActiveMessages()),
    loadAddress: () => dispatch(loadAddress()),
    getUnreadNotifications: () => dispatch(getUnreadNotifications())
})


//export your list as a default export 
export default connect(mapStateToProps, mapDispatchToProps)(Home);
