import { createStore, applyMiddleware } from "redux";
import thunk from "redux-thunk";
import reducer from "./reducer";
import { fetchRestaurants, fetchRestaurantsError } from "./reducer";
import axios from "axios";
import Consts from "../Consts";
const CREATE_ROUTE = Consts.CREATE_ROUTE;

export const getRestaurants = (location) => {
  return async (dispatch) => {
    axios
      .get(Consts.CREATE_ROUTE(`restaurants/${location}`))
      .then((response) => {
        dispatch(fetchRestaurants(response.data.data));
      })
      .catch((error) => {
        dispatch(fetchRestaurantsError(error));
      });
  };
};

export default createStore(reducer, applyMiddleware(thunk));
