import React, { useState } from 'react';
import './top.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'
import { getRestaurants } from '../../../redux/store';
import { connect } from 'react-redux';

//TODO REVISAR ESTRUCTURA, USAR VARIABLES PARA LAS CATEGORIAS , MOVBILE


function Top({search, }) {
    return (
        <div id="list-top">
            <div id="top-inner">

                {props.search ?
                    <div  className="title-cont">
                        <button onClick={()=>{props.getRestaurants(props.search)}} id="search-butt">Search</button>
                    </div> :
                    <div id="search-butt-cont"> 
                      <span className="header-title">{props.page[0].toUpperCase() + props.page.slice(1)}</span>  
                    </div>
                    }

                <div className="search-cont">
                    <input id="search" placeholder={`Search by ${props.page}`}  value={props.search} onChange={props.searchChange}/>
                    <FontAwesomeIcon className="searchIcon" icon={faSearch} />
                </div>
            </div>
        </div>

    )
}


const mapStateToProps = state => ({
})

//Map your action creators to your props.
const mapDispatchToProps = dispatch => ({
    getRestaurants: (location) => dispatch(getRestaurants(location))
})


export default connect(mapStateToProps, mapDispatchToProps)(Top);
