import React from "react";
import "./detail.css";
import Icon from "../../assets/ForkIcon";
import { connect } from "react-redux";
import { setSelectedItem } from "../../redux/store";

function Detail({ selectedItem, setSelectedItem }) {
  return (
    <div id="detail-container">
      {!selectedItem ? (
        <div id="no-data-cont">
          <Icon />
          <span id="icon-detail">Select a restaurant to display it here!</span>
        </div>
      ) : (
        <div id="data-cont">
            {/* <span>{selectedItem.name}</span>
            <span>{selectedItem.categories[0]}</span>
            <span>{selectedItem.display_phone}</span>
            <span>{selectedItem.is_closed}</span>
            <span>{selectedItem.location}</span>
            <span>{selectedItem.price}</span>
            <span>{selectedItem.rating}</span>
            <span>{selectedItem.review}</span> */}
        </div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => ({
  selectedItem: state.selectedItem,
});

const mapDispatchToProps = (dispatch) => ({
  setSelectedItem: (item) => dispatch(setSelectedItem(item)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Detail);
