import React from 'react';
import './side-bar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUtensils } from '@fortawesome/free-solid-svg-icons'
import { faList } from '@fortawesome/free-solid-svg-icons'
import { getRestaurants } from '../../redux/store';
import { connect } from 'react-redux';

function SideBar(page, getRestaurants, setPage, setSearch) {
    const handleOnClick = (destination) => {
        props.getRestaurants([])
        props.setPage(destination)
        props.setSearch("")
    }

    return (

        <div id="side-bar">
            {props.page === "location" ?
                <div id="side-inner">
                    <div className="icon-cont icon-clicked" onClick={() => handleOnClick("location")}>
                        <FontAwesomeIcon class="icon" icon={faUtensils} />
                    </div>
                    <div className="icon-cont icon-cont-down" onClick={() => handleOnClick("categories")}>
                        <FontAwesomeIcon class="icon" icon={faList} />
                    </div>
                </div>
                :
                <div id="side-inner">
                    <div className="icon-cont icon-cont-up" onClick={() => handleOnClick("location")}>
                        <FontAwesomeIcon class="icon" icon={faUtensils} />
                    </div>
                    <div className="icon-cont icon-clicked" onClick={() => handleOnClick("categories")}>
                        <FontAwesomeIcon class="icon" icon={faList} />
                    </div>
                </div>
            }



        </div>


    )
}



const mapStateToProps = state => ({
})

const mapDispatchToProps = dispatch => ({
    getRestaurants: (content) => dispatch(getRestaurants(content))
})

export default connect(mapStateToProps, mapDispatchToProps)(SideBar);
