import React, { useState } from 'react';
import './top.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'
import { getRestaurants } from '../../../redux/store';
import { connect } from 'react-redux';

function Top({search, searchChange, page, getRestaurants}) {
    const handleOnkeyPress = (event) => {
        if(event.key == "Enter"){
            getRestaurants(search)
        }
    }
    getRestaurantsRequest = () => {
        getRestaurants(search)

    }
    return (
        <div id="list-top">
            <div id="top-inner">

                {search ?
                    <div  className="title-cont">
                        <button onClick={()=>{getRestaurants(search)}} id="search-butt">Search</button>
                    </div> :
                    <div id="search-butt-cont"> 
                      <span className="header-title">{page[0].toUpperCase() + page.slice(1)}</span>  
                    </div>
                    }

                <div className="search-cont">
                    <input id="search" onKeyPress={handleOnkeyPress} placeholder={`Search by ${page}`}  value={search} onChange={searchChange}/>
                    <FontAwesomeIcon className="searchIcon" icon={faSearch} />
                </div>
            </div>
        </div>

    )
}

const mapStateToProps = state => ({
})

const mapDispatchToProps = dispatch => ({
    getRestaurants: (location) => dispatch(getRestaurants(location))
})

export default connect(mapStateToProps, mapDispatchToProps)(Top);
